import React,{useRef, useState} from 'react'
import {Col, Form, FormGroup} from 'reactstrap';
import './search-bar.css';

import { BASE_URL } from './../utils/config.js';
import {useNavigate} from "react-router-dom";

const SearchBar = () => {
    const [loc, setLoc] = useState('')
    const locationRef= useRef('');
    const distanceRef= useRef(0);
    const maxGroupSizeRef= useRef(0);
    const navigate = useNavigate();

    async function searchHandler() {
        const location = loc;
        const distance = distanceRef.current.value;
        const maxGroupSize = maxGroupSizeRef.current.value;

        if (location === '' || distance === '' || maxGroupSize === '')
            return alert("All Fields are required!!!");

        const res = await fetch(`${BASE_URL}/tours/search/getTourBySearch?city=${location}
        &distance=${distance}&maxGroupSize=${maxGroupSize}`);

        if (!res.ok)
            alert("Something went wrong");

        const result = await res.json();
        console.log(location)
        navigate(
            `/tours/search?city=${location}&distance=${distance}&maxGroupSize=${maxGroupSize}`,
            { state: result.data }
        );
};


  return (
    <Col lg='12'>
        <div className="search__bar">
            <Form className='d-flex align-items-center gap-4'>
                <FormGroup className='d-flex gap-3 form__group form__group-fast'>
                    <span><i class="ri-map-pin-line"></i></span>
                    <div>
                        <h6>Location</h6>
                        <input type="text" placeholder="Where are you going?" onChange={(e) => setLoc(e.target.value)} ref={locationRef}/>
                    </div>
                </FormGroup>
                <FormGroup className='d-flex gap-3 form__group form__group-fast'>
                    <span><i class="ri-map-pin-time-line"></i></span>
                    <div>
                        <h6>Distance</h6>
                        <input type="number" placeholder="Distance k/m" ref={distanceRef}/>
                    </div>
                </FormGroup>
                <FormGroup className='d-flex gap-3 form__group form__group-last'>
                    <span><i class="ri-group-line"></i></span>
                    <div>
                        <h6>Max People</h6>
                        <input type="number" placeholder="0" ref={maxGroupSizeRef}/>
                    </div>
                </FormGroup>

                <span className="search__icon" type="submit" onClick={searchHandler} >
                    <i class="ri-search-line"></i>
                </span>

            </Form>
        </div>
    </Col>
  )
}

export default SearchBar;
